import React, { useState } from "react";
import "./newExpense.css";
import ExpenseForm from "../ExpenseForm/expenseForm";
import "./ExpenseForm/expenseForm.css";
const NewExpense = (newExp) => {
  const SaveNewExpenseHandler = (EnteredExpenseData) => {
    const NewExpenseData = {
      ...EnteredExpenseData,
      id: Math.random().toString(),
    };
    newExp.onNewExpense(NewExpenseData);
  };

  const [buttonTask, setButtonTask] = useState(false);
  const ChangeButtonTask = () => {
    setButtonTask(true);
  };
  if (buttonTask === false) {
    return <button onClick={ChangeButtonTask}>Add New Expense</button>;
  } else {
    return (
      <div className="new-expense">
        <ExpenseForm onSaveNewExpense={SaveNewExpenseHandler} />
      </div>
    );
  }
};
export default NewExpense;
